mapper.select:
SELECT scc_info.scc_name, scc_info.scc_grade, scc_info.scc_address, cart.sccNum
 FROM cart, scc_info
 where cart.userNo = #{userNo} and cart.sccNum = scc_info.scc_num;

mapper.delete:
delete
from cart
where sccNum = #{sccNum} AND userNo = #{userNo}

mapper.select:
select count(cartNum) as cartIn
from cart
where sccNum = #{sccNum} AND userNo = #{userNo}
