mapper.update:
UPDATE board2
   SET title =#{title},
	   content =#{content}
 WHERE boardNo = #{boardNo}

mapper.insert:
INSERT INTO pds_attach
	 (
	   fullName,
	   boardNo
	 )
VALUES
	 (
	   #{fullName},
	   #{boardNo}
	 )

mapper.update:
UPDATE pds_attach
   SET down_cnt = down_cnt + 1
 WHERE fullName = #{fullName}

UPDATE board2
   SET view_cnt = view_cnt + 1
 WHERE boardNo = #{boardNo}
